import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Home = ({settext}) => {
  const [files, setFiles] = useState([]);
  const nav=useNavigate();
  const handleButtonClick = () => {
    const newFile = document.createElement("input");
    newFile.type = "file";
    newFile.id = `file-${files.length + 1}`;

    newFile.addEventListener('change', (event) => {
      const selectedFile = event.target.files[0];
      console.log(selectedFile);

      // Use the functional form of setFiles to ensure you are updating based on the previous state
      setFiles(prevFiles => [...prevFiles, selectedFile]);
    });

    newFile.click();
  };
  const submitFiles= async()=>{
    console.log("called");
    console.log(typeof settext);
    console.log(settext);
    let headersList = {
      "Access-Control-Allow-Headers": "Content-Type",
      "Access-Control-Allow-Origin": "*",
     }
     
     let bodyContent = new FormData();
     for(let i of files)
     {
      bodyContent.append("file" ,i);
     }
     console.log("befor");
     try {
      let response = await fetch("http://127.0.0.1:5000/summary", { 
        method: "POST",
        body: bodyContent,
        headers: headersList
      });
    
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
    
      let data = await response.text();
      console.log(data);
      console.log(JSON.parse(data));
      settext(data);
      nav("/summary");

    } catch (error) {
      console.error('Fetch error:', error);
      if (error.response) {
        console.error('Response status:', error.response.status);
      }
    }
    
  }
  return (
    <div className='total'>
      
        <div className='center_div'>
          <div className='center_div_child'>
            <button className='add_file' onClick={handleButtonClick}>Add Files</button>
            <div className='file_names_cover'>
              {files.map((file, index) => (
                <div className='file_names' key={index}>{file.name}</div>
              ))}
            </div>
            <form onSubmit={submitFiles}>
            <input type='submit' className='btn btn-success'/>
            </form>
          </div>
        </div>
      
    </div>
  );
};

export default Home;
